import React, { Component } from 'react';
import Yokai from "../static/yokai.png"
import Kaoru from "../static/kaoru.png"

class Artwork extends Component {
  static getInitialProps ({ query: { id } }) {
    return { postId: id }
  }

    render() {
      return <> 
      <div className="layout">
      <div className="submission">
      <div className="art">
      <img className="imagetest" src={Kaoru}></img>
      </div>
      
      <h1>{this.props.postId}</h1>
      </div>
      </div>
      <div className="sidecolumn">
        <div className="side">
        <p>one</p>
				<p>two</p>
				<p>three</p>
				<p>four</p>
				<p>five</p>
				<p>six</p>
				<p>seven</p>
				<p>eight</p>
				<p>nine</p>
				<p>ten</p>
				<p>eleven</p>
				<p>twelve</p>
				<p>thirteen</p>
				<p>fourteen</p>
				<p>fifteen</p>
				<p>sixteen</p>
				<p>seventeen</p>
				<p>eighteen</p>
				<p>nineteen</p>
				<p>twenty</p>
				<p>twenty-one</p>
				<p>twenty-two</p>
				<p>twenty-three</p>
				<p>twenty-four</p>
				<p>twenty-five</p>
				<p>twenty-six</p>
				<p>twenty-seven</p>
				<p>twenty-eight</p>
				<p>twenty-nine</p>
				<p>thirty</p>
        </div>
      </div>

      <style jsx global>{`
  .navposition{
    position: fixed;
  }
  `}</style>
      <style jsx>{`

.imagetest{
  display: block;
  max-width: 100%;
  height: auto;
  max-height: calc(100vh - 70px);
}

.art{
  
}

.layout {
  height: 100%;  /*allows both columns to span the full height of the browser window*/
  display: flex;
  flex-direction: row;  /*places the left and right headers above the bottom content*/
  width: 100%;
  -ms-flex: 1 1 0.000000001px;
  -webkit-flex: 1;
  flex: 1;
  -webkit-flex-basis: 0.000000001px;
  flex-basis: 0.000000001px;
  flex-grow: 1;  /*ensures that the container will take up the full height of the parent container*/
  overflow-y: auto;  /*adds scroll to this container*/
  justify-content:flex-end;
}

.submission{
  width:100%;
  justify-content:center;
  align-items: center;
  display: flex;
  flex-direction: column;
  -ms-flex: 1 1 0.000000001px;
  -webkit-flex: 1;
  flex: 1;
  -webkit-flex-basis: 0.000000001px;
  flex-basis: 0.000000001px;
  flex-grow: 1;  /*ensures that the container will take up the full height of the parent container*/
  overflow-y: auto;  /*adds scroll to this container*/
}

.sidecolumn {
  height: 100%;  /*allows both columns to span the full height of the browser window*/
  display: flex;
  flex-direction: column;  /*places the left and right headers above the bottom content*/
  width: 399px;
  
}

.side {
  flex-grow: 1;  /*ensures that the container will take up the full height of the parent container*/
  overflow-y: auto;  /*adds scroll to this container*/
  
}

`}</style>


      </>;
    }
  }
  
  export default Artwork;
